import { useState } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams
} from 'react-router-dom';

import Education from './../components/education';
import Projects from './../components/projects';
import About from './../components/about';
import Skills from './../components/skills';
import Resume from './../components/resume';

import './rightCard.css';
import logo from './../logo/call.png';


function RightCard() {
    let navBar = ["About", "Education", "Projects", "Skills", "Resume"];
    const indOrigin = navBar[0];

    let [data, setData] = useState("About");

    const navBarUpdate = (data)=> {
        console.log(data);
        let ind = navBar.indexOf(data);
        navBar[0] = data;
        navBar[ind] = indOrigin;
    }

    return (
        <div className="sub-container-2">
            <Router>
                <div className="nav-bar">
                    <Link
                        className="abt"
                        to="/"
                        onClick={() => {
                            setData("About");
                            navBarUpdate("About");
                        }}
                    >{navBar[0]}
                    </Link>
                    <div>
                        <Link
                            to="/education"
                            onClick={() => {
                                setData("Education");
                                navBarUpdate("Education");
                            }}
                        >{navBar[1]}
                        </Link>
                        <Link
                            to="/projects"
                            onClick={() => {
                                setData("Projects");
                                navBarUpdate("Projects");
                            }}
                        >{navBar[2]}
                        </Link>
                        <Link
                            to="/skills"
                            onClick={() => {
                                setData("Skills");
                                navBarUpdate("Skills");
                            }}
                        >{navBar[3]}
                        </Link>
                        <Link
                            to="/resume"
                            onClick={() => {
                                setData("Resume");
                                navBarUpdate("Resume");
                            }}
                        >{navBar[4]}
                        </Link>
                    </div>
                </div>
                <>
                    <Switch>
                        <Route exact path="/">
                            <About />
                        </Route>
                        <Route path="/education">
                            <Education />
                        </Route>
                        <Route path="/projects">
                            <Projects />
                        </Route>
                        <Route path="/skills">
                            <Skills />
                        </Route>
                        <Route path="/resume">
                            <Resume />
                        </Route>
                    </Switch>
                </>
            </Router>
        </div>
    );
}

export default RightCard;
